mapper.select:
select article.* , catalog.catalogname from article , catalog where article.catalogid = catalog.catalogid
and article.catalogid =#{catalogid}
and ( article.title like CONCAT('%',#{keyword},'%')
)
order by articleid desc
limit #{start},#{limit}

select count(1) from article , catalog where article.catalogid = catalog.catalogid
and article.catalogid =#{catalogid}
and ( article.title like CONCAT('%',#{keyword},'%')
)

select a.* , b.catalogname from article a , catalog b where a.catalogid =
b.catalogid

    and a.title = #{title}


    and a.image = #{image}


    and a.catalogid = #{catalogid}


    and a.istop = #{istop}


    and a.isflv = #{isflv}


    and a.contents = #{contents}


    and a.addtime = #{addtime}


    and a.hits = #{hits}
